                  code: "import React from 'react'\nimport { flattenConnection } from '@shopify/hydrogen'\n\n/**\n * the google limit is 50K, however, the storefront API\n * allows querying only 250 resources per pagination page\n */\nconst MAX_URLS = 250\n\n/**\n * @param {LoaderFunctionArgs}\n */\nexport async function loader({\n  request,\n  context: { storefront },\n}) {\n  const data = await storefront.query(SITEMAP_QUERY, {\n    variables: {\n      urlLimits: MAX_URLS,\n      language: storefront.i18n.language,\n    },\n  })\n\n  if (!data) {\n    throw new Response('No data found', { status: 404 })\n  }\n\n  const sitemap = generateSitemap({\n    data,\n    baseUrl: new URL(request.url).origin,\n  })\n\n  return new Response(sitemap, {\n    headers: {\n      'Content-Type': 'application/xml',\n\n      'Cache-Control': `max-age=${60 * 60 * 24}`,\n    },\n  })\n}\n\n/**\n * @param {string} string\n */\nfunction xmlEncode(string) {\n  return string.replace(\n    /[&<>'\"]/g,\n    (char) => `&#${char.charCodeAt(0)};`,\n  )\n}\n\n/**\n * @param {{\n *   data: SitemapQuery;\n *   baseUrl: string;\n * }}\n */\nfunction generateSitemap({ data, baseUrl }) {\n  const products = flattenConnection(data.products)\n    .filter((product) => product.onlineStoreUrl)\n    .map((product) => {\n      const url = `${baseUrl}/products/${xmlEncode(\n        product.handle,\n      )}`\n\n      const productEntry = {\n        url,\n        lastMod: product.updatedAt,\n        changeFreq: 'daily',\n      }\n\n      if (product.featuredImage?.url) {\n        productEntry.image = {\n          url: xmlEncode(product.featuredImage.url),\n        }\n\n        if (product.title) {\n          productEntry.image.title = xmlEncode(\n            product.title,\n          )\n        }\n\n        if (product.featuredImage.altText) {\n          productEntry.image.caption = xmlEncode(\n            product.featuredImage.altText,\n          )\n        }\n      }\n\n      return productEntry\n    })\n\n  const collections = flattenConnection(data.collections)\n    .filter((collection) => collection.onlineStoreUrl)\n    .map((collection) => {\n      const url = `${baseUrl}/collections/${collection.handle}`\n\n      return {\n        url,\n        lastMod: collection.updatedAt,\n        changeFreq: 'daily',\n      }\n    })\n\n  const pages = flattenConnection(data.pages)\n    .filter((page) => page.onlineStoreUrl)\n    .map((page) => {\n      const url = `${baseUrl}/pages/${page.handle}`\n\n      return {\n        url,\n        lastMod: page.updatedAt,\n        changeFreq: 'weekly',\n      }\n    })\n\n  const urls = [...products, ...collections, ...pages]\n\n  return `\n    <urlset\n      xmlns=\"http://www.sitemaps.org/schemas/sitemap/0.9\"\n      xmlns:image=\"http://www.google.com/schemas/sitemap-image/1.1\"\n    >\n      ${urls.map(renderUrlTag).join('')}\n    </urlset>`\n}\n\n/**\n * @param {Entry}\n */\nfunction renderUrlTag({ url, lastMod, changeFreq, image }) {\n  const imageTag = image\n    ? `<image:image>\n        <image:loc>${image.url}</image:loc>\n        <image:title>${image.title ?? ''}</image:title>\n        <image:caption>${\n          image.caption ?? ''\n        }</image:caption>\n      </image:image>`.trim()\n    : ''\n\n  return `\n    <url>\n      <loc>${url}</loc>\n      <lastmod>${lastMod}</lastmod>\n      <changefreq>${changeFreq}</changefreq>\n      ${imageTag}\n    </url>\n  `.trim()\n}\n\nconst SITEMAP_QUERY = `#graphql\n  query Sitemap($urlLimits: Int, $language: LanguageCode)\n  @inContext(language: $language) {\n    products(\n      first: $urlLimits\n      query: \"published_status:'online_store:visible'\"\n    ) {\n      nodes {\n        updatedAt\n        handle\n        onlineStoreUrl\n        title\n        featuredImage {\n          url\n          altText\n        }\n      }\n    }\n    collections(\n      first: $urlLimits\n      query: \"published_status:'online_store:visible'\"\n    ) {\n      nodes {\n        updatedAt\n        handle\n        onlineStoreUrl\n      }\n    }\n    pages(first: $urlLimits, query: \"published_status:'published'\") {\n      nodes {\n        updatedAt\n        handle\n        onlineStoreUrl\n      }\n    }\n  }\n`\n\n/**\n * @typedef {{\n *   url: string;\n *   lastMod?: string;\n *   changeFreq?: string;\n *   image?: {\n *     url: string;\n *     title?: string;\n *     caption?: string;\n *   };\n * }} Entry\n */\n\n/** @typedef {import('@shopify/remix-oxygen').LoaderFunctionArgs} LoaderFunctionArgs */\n/** @typedef {import('storefrontapi.generated').SitemapQuery} SitemapQuery */\n/** @typedef {import('@shopify/remix-oxygen').SerializeFrom<typeof loader>} LoaderReturnData */\n",
