                  code: "import React from 'react'\nimport { json, redirect } from '@shopify/remix-oxygen'\nimport { useLoaderData } from '@remix-run/react'\nimport {\n  Money,\n  Image,\n  flattenConnection,\n} from '@shopify/hydrogen'\nimport { CUSTOMER_ORDER_QUERY } from '~/graphql/customer-account/CustomerOrderQuery'\n\n/**\n * @type {MetaFunction<typeof loader>}\n */\nexport const meta = ({ data }) => {\n  return [{ title: `Order ${data?.order?.name}` }]\n}\n\n/**\n * @param {LoaderFunctionArgs}\n */\nexport async function loader({ params, context, request }) {\n  if (!params.id) {\n    return redirect('/account/orders')\n  }\n\n  const orderId = atob(params.id)\n  const { data, errors } =\n    await context.customerAccount.query(\n      CUSTOMER_ORDER_QUERY,\n      {\n        variables: { orderId },\n      },\n    )\n\n  if (errors?.length || !data?.order) {\n    throw new Error('Order not found')\n  }\n\n  const { order } = data\n\n  const lineItems = flattenConnection(order.lineItems)\n  const discountApplications = flattenConnection(\n    order.discountApplications,\n  )\n  const fulfillmentStatus = flattenConnection(\n    order.fulfillments,\n  )[0].status\n\n  const firstDiscount = discountApplications[0]?.value\n\n  const discountValue =\n    firstDiscount?.__typename === 'MoneyV2' && firstDiscount\n\n  const discountPercentage =\n    firstDiscount?.__typename ===\n      'PricingPercentageValue' && firstDiscount?.percentage\n\n  return json(\n    {\n      order,\n      lineItems,\n      discountValue,\n      discountPercentage,\n      fulfillmentStatus,\n    },\n    {\n      headers: {\n        'Set-Cookie': await context.session.commit(),\n      },\n    },\n  )\n}\n\nexport default function OrderRoute() {\n  /** @type {LoaderReturnData} */\n  const {\n    order,\n    lineItems,\n    discountValue,\n    discountPercentage,\n    fulfillmentStatus,\n  } = useLoaderData()\n  return (\n    <div className='account-order'>\n      <h2>Order {order.name}</h2>\n      <p>\n        Placed on{' '}\n        {new Date(order.processedAt).toDateString()}\n      </p>\n      <br />\n      <div>\n        <table>\n          <thead>\n            <tr>\n              <th scope='col'>Product</th>\n              <th scope='col'>Price</th>\n              <th scope='col'>Quantity</th>\n              <th scope='col'>Total</th>\n            </tr>\n          </thead>\n          <tbody>\n            {lineItems.map((lineItem, lineItemIndex) => (\n              // eslint-disable-next-line react/no-array-index-key\n              <OrderLineRow\n                key={lineItemIndex}\n                lineItem={lineItem}\n              />\n            ))}\n          </tbody>\n          <tfoot>\n            {((discountValue && discountValue.amount) ||\n              discountPercentage) && (\n              <tr>\n                <th scope='row' colSpan={3}>\n                  <p>Discounts</p>\n                </th>\n                <th scope='row'>\n                  <p>Discounts</p>\n                </th>\n                <td>\n                  {discountPercentage ? (\n                    <span>-{discountPercentage}% OFF</span>\n                  ) : (\n                    discountValue && (\n                      <Money data={discountValue} />\n                    )\n                  )}\n                </td>\n              </tr>\n            )}\n            <tr>\n              <th scope='row' colSpan={3}>\n                <p>Subtotal</p>\n              </th>\n              <th scope='row'>\n                <p>Subtotal</p>\n              </th>\n              <td>\n                <Money data={order.subtotal} />\n              </td>\n            </tr>\n            <tr>\n              <th scope='row' colSpan={3}>\n                Tax\n              </th>\n              <th scope='row'>\n                <p>Tax</p>\n              </th>\n              <td>\n                <Money data={order.totalTax} />\n              </td>\n            </tr>\n            <tr>\n              <th scope='row' colSpan={3}>\n                Total\n              </th>\n              <th scope='row'>\n                <p>Total</p>\n              </th>\n              <td>\n                <Money data={order.totalPrice} />\n              </td>\n            </tr>\n          </tfoot>\n        </table>\n        <div>\n          <h3>Shipping Address</h3>\n          {order?.shippingAddress ? (\n            <address>\n              <p>{order.shippingAddress.name}</p>\n              {order.shippingAddress.formatted ? (\n                <p>{order.shippingAddress.formatted}</p>\n              ) : (\n                ''\n              )}\n              {order.shippingAddress.formattedArea ? (\n                <p>{order.shippingAddress.formattedArea}</p>\n              ) : (\n                ''\n              )}\n            </address>\n          ) : (\n            <p>No shipping address defined</p>\n          )}\n          <h3>Status</h3>\n          <div>\n            <p>{fulfillmentStatus}</p>\n          </div>\n        </div>\n      </div>\n      <br />\n      <p>\n        <a\n          target='_blank'\n          href={order.statusPageUrl}\n          rel='noreferrer'\n        >\n          View Order Status →\n        </a>\n      </p>\n    </div>\n  )\n}\n\n/**\n * @param {{lineItem: OrderLineItemFullFragment}}\n */\nfunction OrderLineRow({ lineItem }) {\n  return (\n    <tr key={lineItem.id}>\n      <td>\n        <div>\n          {lineItem?.image && (\n            <div>\n              <Image\n                data={lineItem.image}\n                width={96}\n                height={96}\n              />\n            </div>\n          )}\n          <div>\n            <p>{lineItem.title}</p>\n            <small>{lineItem.variantTitle}</small>\n          </div>\n        </div>\n      </td>\n      <td>\n        <Money data={lineItem.price} />\n      </td>\n      <td>{lineItem.quantity}</td>\n      <td>\n        <Money data={lineItem.totalDiscount} />\n      </td>\n    </tr>\n  )\n}\n\n/** @typedef {import('@shopify/remix-oxygen').LoaderFunctionArgs} LoaderFunctionArgs */\n/** @template T @typedef {import('@remix-run/react').MetaFunction<T>} MetaFunction */\n/** @typedef {import('customer-accountapi.generated').OrderLineItemFullFragment} OrderLineItemFullFragment */\n/** @typedef {import('@shopify/remix-oxygen').SerializeFrom<typeof loader>} LoaderReturnData */\n",
