                  code: "import React from 'react'\nimport {Link, useLoaderData} from '@remix-run/react';\nimport {\n  Money,\n  Pagination,\n  getPaginationVariables,\n  flattenConnection,\n} from '@shopify/hydrogen';\nimport {json} from '@shopify/remix-oxygen';\nimport {CUSTOMER_ORDERS_QUERY} from '~/graphql/customer-account/CustomerOrdersQuery';\n\n/**\n * @type {MetaFunction}\n */\nexport const meta = () => {\n  return [{title: 'Orders'}];\n};\n\n/**\n * @param {LoaderFunctionArgs}\n */\nexport async function loader({request, context}) {\n  const paginationVariables = getPaginationVariables(request, {\n    pageBy: 20,\n  });\n\n  const {data, errors} = await context.customerAccount.query(\n    CUSTOMER_ORDERS_QUERY,\n    {\n      variables: {\n        ...paginationVariables,\n      },\n    },\n  );\n\n  if (errors?.length || !data?.customer) {\n    throw Error('Customer orders not found');\n  }\n\n  return json(\n    {customer: data.customer},\n    {\n      headers: {\n        'Set-Cookie': await context.session.commit(),\n      },\n    },\n  );\n}\n\nexport default function Orders() {\n  /** @type {LoaderReturnData} */\n  const {customer} = useLoaderData();\n  const {orders} = customer;\n  return (\n    <div className=\"orders\">\n      {orders.nodes.length ? <OrdersTable orders={orders} /> : <EmptyOrders />}\n    </div>\n  );\n}\n\n/**\n * @param {Pick<CustomerOrdersFragment, 'orders'>}\n */\nfunction OrdersTable({orders}) {\n  return (\n    <div className=\"acccount-orders\">\n      {orders?.nodes.length ? (\n        <Pagination connection={orders}>\n          {({nodes, isLoading, PreviousLink, NextLink}) => {\n            return (\n              <>\n                <PreviousLink>\n                  {isLoading ? 'Loading...' : <span>↑ Load previous</span>}\n                </PreviousLink>\n                {nodes.map((order) => {\n                  return <OrderItem key={order.id} order={order} />;\n                })}\n                <NextLink>\n                  {isLoading ? 'Loading...' : <span>Load more ↓</span>}\n                </NextLink>\n              </>\n            );\n          }}\n        </Pagination>\n      ) : (\n        <EmptyOrders />\n      )}\n    </div>\n  );\n}\n\nfunction EmptyOrders() {\n  return (\n    <div>\n      <p>You haven&apos;t placed any orders yet.</p>\n      <br />\n      <p>\n        <Link to=\"/collections\">Start Shopping →</Link>\n      </p>\n    </div>\n  );\n}\n\n/**\n * @param {{order: OrderItemFragment}}\n */\nfunction OrderItem({order}) {\n  const fulfillmentStatus = flattenConnection(order.fulfillments)[0]?.status;\n  return (\n    <>\n      <fieldset>\n        <Link to={`/account/orders/${btoa(order.id)}`}>\n          <strong>#{order.number}</strong>\n        </Link>\n        <p>{new Date(order.processedAt).toDateString()}</p>\n        <p>{order.financialStatus}</p>\n        {fulfillmentStatus && <p>{fulfillmentStatus}</p>}\n        <Money data={order.totalPrice} />\n        <Link to={`/account/orders/${btoa(order.id)}`}>View Order →</Link>\n      </fieldset>\n      <br />\n    </>\n  );\n}\n\n/** @template T @typedef {import('@remix-run/react').MetaFunction<T>} MetaFunction */\n/** @typedef {import('@shopify/remix-oxygen').LoaderFunctionArgs} LoaderFunctionArgs */\n/** @typedef {import('customer-accountapi.generated').CustomerOrdersFragment} CustomerOrdersFragment */\n/** @typedef {import('customer-accountapi.generated').OrderItemFragment} OrderItemFragment */\n/** @typedef {import('@shopify/remix-oxygen').SerializeFrom<typeof loader>} LoaderReturnData */\n",
